import React from 'react'
import { BsFillArrowRightCircleFill, BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState, useEffect, useRef } from 'react'
import GuestForm from './GuestForm';
import axios from 'axios'

const Reservation = () => {
  let current = new Date()
  let currentdate = current.toDateString()
  const [days, setDays] = useState(1)
  const [cdate, setcDate] = useState(currentdate)
  const [times, setTimes] = useState([])
  const [reserve, setReserve] = useState(false)
  const [data, setData] = useState([])
  const [dateChosen, setDateChosen] = useState(false)
  const endpoint = 'http://localhost:4000/app/reservations'
  const dataFetchedRef = useRef(false);
  const displayTimes = ['8:00 AM', '8:30 AM', '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM', '12:00 PM',
    '12:30 PM', '1:00 PM', '1:30 PM', '2:00 PM', '2:30 PM', '3:00 PM', '3:30 PM', '4:00 PM', '4:30 PM', '5:00 PM', '5:30 PM',
    '6:00 PM', '6:30 PM', '7:00 PM', '7:30 PM', '8:00 PM', '8:30 PM', '9:00 PM', '9:30 PM', '10:00 PM']

  //-------------------- Get Request --------------------
  function getRequest () {
    axios.get(endpoint, {})
    .then(res => {
      const resdata = res.data
      setData(resdata)
      //--------------- for rendering the first day -------------
      if(cdate === currentdate)
        renderTimeSlots(resdata)
    })
    .catch((error) =>
        console.log('Error sending data:', error)
  )}

  //--------------------calls api once at render------------------------------
  useEffect(() => {
    if (dataFetchedRef.current) 
      return;
    dataFetchedRef.current = true
    getRequest()
  })

  function formatTimes(reservedTimes){
    const arr = displayTimes.filter(i => !reservedTimes.includes(i))
    let timeRows = []
    let tempRow = []

    let count = 0
    for(let i = 0; i < arr.length; i++){
      count++
      tempRow.push(arr[i])
      if((count === 3)){
        timeRows.push(tempRow)
        tempRow = []
        count = 0
      }
      if(i === arr.length - 1 && count > 0)
        timeRows.push(tempRow)
    }
    return timeRows
  }

  function renderTimeSlots(resdata){
    const currentDay = resdata.filter(({date}) => date === cdate)
    const reservedTimes = currentDay.map(value => value.time)
    const timeSlots = formatTimes(reservedTimes)
    setTimes(timeSlots)
  }

  function nextDate () {
    current.setDate(current.getDate() + days)
    currentdate = current.toDateString()

    setDays(days + 1)
    setcDate(currentdate)

    const currentDay = data.filter(({date}) => date === currentdate)
    const reservedTimes = currentDay.map(value => value.time)
    const timeSlots = formatTimes(reservedTimes)
    setTimes(timeSlots)
  }

  function previousDate () {
    current.setDate(current.getDate() + (days - 2))
    currentdate = current.toDateString()

    setDays(days - 1)
    setcDate(currentdate)

    const currentDay = data.filter(({date}) => date === currentdate)
    const reservedTimes = currentDay.map(value => value.time)
    const timeSlots = formatTimes(reservedTimes)
    setTimes(timeSlots)
  } 

  const displayTimeSlots = times.map((ele, i) => {
    return(
      <div className='center' key={i}>
        {ele[0] && <button type="button" className="btn" value={ele[0]} onClick={() => setDateChosen(true)}>{ele[0]}</button>}
        {ele[1] && <button type="button" className="btn" value={ele[1]} onClick={() => setDateChosen(true)}>{ele[1]}</button>}
        {ele[2] && <button type="button" className="btn" value={ele[2]} onClick={() => setDateChosen(true)}>{ele[2]}</button>}
      </div>
    )
  })

  const handleReservation = (e) => {
/*
    const reservation = {
      first: firstName,
      last: lastName,
      phone: phone,
      email: email,
      date: date,
      time: time,
      guests: guests
    }*/
  }

  return (
    <div className="prevent-select">
      <h1 className='sub-header'>Reservation</h1>

      {reserve && 
        <div>
          <div className='center'>
            <BsFillArrowLeftCircleFill onClick={() => days !== 1 ? previousDate() : '' } style={{cursor:'pointer'}} />
            {`${' '}${cdate}${' '}`}
            <BsFillArrowRightCircleFill onClick={() => {nextDate();}} style={{cursor:'pointer'}}/>
          </div>
          <div className='section'>
            {displayTimeSlots}
          </div>
        </div>
      }

      {reserve === false && 
       <div className='center'>
          <br></br>
          <button type="button" className="btn" style={{background:'#000', color: '#fff'}} onClick={() => setReserve(true)}>Make a Reservation</button>
        </div>
      }

      {dateChosen && 
        <GuestForm />
      }

    </div>
  )
}

export default Reservation
