import React from 'react'
import { BsFillArrowRightCircleFill, BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState } from 'react'

const Reservation = () => {
  var current = new Date().toDateString()
  console.log(current)
  var currentdate = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`
  const [days, setDays] = useState(1)
  const [date, setDate] = useState(currentdate)

  const [month, day, year] = [current.getMonth(), current.getDate(), current.getFullYear()];
  const d = `${month} ${day}, ${year}`


  function nextdate () {
    current.setDate(current.getDate() + days)
    currentdate = `${current.getDate()}/${current.getMonth()+1}/${current.getFullYear()}`;
    setDays(days + 1)
    setDate(currentdate)
  }

  return (
    <div>
      <h1>Reservation</h1>
      <div className='center'>
        <BsFillArrowLeftCircleFill />
        {date}
        <BsFillArrowRightCircleFill onClick={() => nextdate()} style={{cursor:'pointer'}}/>
      </div>
    </div>
  )
}

export default Reservation
