import React from 'react'
import { BsFillArrowRightCircleFill, BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState } from 'react'
import TimeSlots from './TimeSlots';

const Reservation = () => {
  var current = new Date()
  var currentdate = current.toDateString()
  const [days, setDays] = useState(1)
  const [date, setDate] = useState(currentdate)

  function nextDate () {
    current.setDate(current.getDate() + days)
    currentdate = current.toDateString()

    setDays(days + 1)
    setDate(currentdate)
  }

  function previousDate () {
    current.setDate(current.getDate() + (days - 2))
    currentdate = current.toDateString()

    setDays(days - 1)
    setDate(currentdate)
  } 

  return (
    <div className="prevent-select">
      <h1 id='reservation'>Reservation</h1>

      <div className='center'>
        <BsFillArrowLeftCircleFill onClick={() => days !== 1 ? previousDate() : '' } style={{cursor:'pointer'}} />
        {`${'  '}${date}${'  '}`}
        <BsFillArrowRightCircleFill onClick={() => nextDate()} style={{cursor:'pointer'}}/>
      </div>

      <span className='center-inline'>
        <span className='vertical-center'>
          <BsFillArrowLeftCircleFill onClick={''} style={{cursor:'pointer', float: 'left'}} />
        </span>
        <TimeSlots />
        <span className='vertical-center'>
          <BsFillArrowRightCircleFill onClick={''} style={{cursor:'pointer', float: 'right'}}/>
        </span>
      </span>
    </div>
  )
}

export default Reservation
