import React from 'react'
import { BsFillArrowRightCircleFill, BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState, useEffect } from 'react'
import axios from 'axios'

const Reservation = () => {
  let current = new Date()
  let currentdate = current.toDateString()
  const [days, setDays] = useState(1)
  const [date, setDate] = useState(currentdate)
  const [times, setTimes] = useState([])
  const endpoint = 'http://localhost:4000/app/reservations'
  const displayTimes = ['8:00 AM', '8:30 AM', '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM', '12:00 PM',
    '12:30 PM', '1:00 PM', '1:30 PM', '2:00 PM', '2:30 PM', '3:00 PM', '3:30 PM', '4:00 PM', '4:30 PM', '5:00 PM', '5:30 PM',
    '6:00 PM', '6:30 PM', '7:00 PM', '7:30 PM', '8:00 PM', '8:30 PM', '9:00 PM', '9:30 PM', '10:00 PM']


  function nextDate () {
    current.setDate(current.getDate() + days)
    currentdate = current.toDateString()

    setDays(days + 1)
    setDate(currentdate)
  }

  function previousDate () {
    current.setDate(current.getDate() + (days - 2))
    currentdate = current.toDateString()

    setDays(days - 1)
    setDate(currentdate)
  } 

  function getRequest () {
    axios.get(endpoint, {})
    .then(res => {
      const reservedTimes = res.data.map(value => value.time)
      const filteredTimes = displayTimes.filter(i => !reservedTimes.includes(i))
      let newArr = []
      let x = []

      let count = 0
      for(let i = 0; i < filteredTimes.length; i++){
        count++
        x.push(filteredTimes[i])
        if((count == 3)){
          newArr.push(x)
          x = []
          count = 0
        }
        if(i === filteredTimes.length - 1 && count > 0)
          newArr.push(x)
      }

      setTimes(filteredTimes)
    })
    .catch((error) =>
        console.log('Error sending data:', error))
    }

    useEffect(() => getRequest(), []);

    const arr = times.map((ele, i, arr) => {
      return(
        <div className='center'>
          <button type="button" class="btn btn-outline-dark" key={i}>{arr[i]}</button>
          <button type="button" class="btn btn-outline-dark" key={i+1}>{arr[i+1]}</button>
          <button type="button" class="btn btn-outline-dark" key={i+2}>{arr[i+2]}</button>
        </div>
      )
    })


  return (

    <div className="prevent-select">
      <h1 id='reservation'>Reservation</h1>

      <div className='center'>
        <BsFillArrowLeftCircleFill onClick={() => days !== 1 ? previousDate() : '' } style={{cursor:'pointer'}} />
        {`${' '}${date}${' '}`}
        <BsFillArrowRightCircleFill onClick={() => nextDate()} style={{cursor:'pointer'}}/>
      </div>

      <span className='center-inline'>
        <span className='vertical-center'>
          <BsFillArrowLeftCircleFill onClick={''} style={{cursor:'pointer', float: 'left'}} />
        </span>

        <span>
          {arr}
        </span>

        <span className='vertical-center'>
          <BsFillArrowRightCircleFill onClick={''} style={{cursor:'pointer', float: 'right'}}/>
        </span>
      </span>
    </div>
  )
}

export default Reservation
