import React from 'react'
import { BsFillArrowRightCircleFill, BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState } from 'react'

const Reservation = () => {
  const today = new Date()
  var current = new Date()
  var currentdate 
  const [days, setDays] = useState(1)
  const [date, setDate] = useState(today.toDateString())

  function nextDate () {
    current.setDate(current.getDate() + days)
    currentdate = current.toDateString()

    setDays(days + 1)
    setDate(currentdate)
  }

  function previousDate () {
    current.setDate(current.getDate() - 1)
    currentdate = current.toDateString()

    setDays(days - 1)
    setDate(currentdate)
  } 

  return (
    <div>
      <h1>Reservation</h1>
      <div className='center'>
        <BsFillArrowLeftCircleFill onClick={() => {days !== 1 ? previousDate() : '' 
      console.log(currentdate)}} style={{cursor:'pointer'}} />
        {date}
        <BsFillArrowRightCircleFill onClick={() => nextDate()} style={{cursor:'pointer'}}/>
      </div>
    </div>
  )
}

export default Reservation
